import React, { useState } from "react";
import "./App.css";
import Duration from "./pomodoro/Duration";
import Play from "./pomodoro/Play";
import Pomodoro from "./pomodoro/Pomodoro";

function App() {
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusDuration, setFocusDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [remainingFocusTime, setRemainingFocusTime] = useState(focusDuration);
  const [remainingBreakTime, setRemainingBreakTime] = useState(breakDuration);

  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <div className="pomodoro">
          <Duration
            isTimerRunning={isTimerRunning}
            setIsTimerRunning={setIsTimerRunning}
            focusDuration={focusDuration}
            setFocusDuration={setFocusDuration}
            breakDuration={breakDuration}
            setBreakDuration={setBreakDuration}
          />
          <Play
            isTimerRunning={isTimerRunning}
            setIsTimerRunning={setIsTimerRunning}
          />
          <Pomodoro
            isTimerRunning={isTimerRunning}
            focusDuration={focusDuration}
            breakDuration={breakDuration}
            remainingFocusTime={remainingFocusTime}
            setRemainingFocusTime={setRemainingFocusTime}
            remainingBreakTime={remainingBreakTime}
            setRemainingBreakTime={setRemainingBreakTime}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
