import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { secondsToDuration } from "../utils/duration";

function Pomodoro() {
  const initialTimerState = {
    play: false,
    pause: false,
    stop: true,
  };

  const [timerState, setTimerState] = useState(initialTimerState);
  const [onBreak, setOnBreak] = useState(false);
  const [focusDuration, setFocusDuration] = useState(1500);
  const [breakDuration, setBreakDuration] = useState(300);
  const [remainingTime, setRemainingTime] = useState(focusDuration);
  const [progressPercent, setProgressPercent] = useState(0);

  function playTimer() {
    setTimerState({
      play: true,
      pause: false,
      stop: false,
    });
  }

  function pauseTimer() {
    setTimerState({
      play: false,
      pause: true,
      stop: false,
    });
  }

  function stopTimer() {
    setTimerState({
      play: false,
      pause: false,
      stop: true,
    });
  }

  function findProgressPercent() {
    if (onBreak) {
      const percent = Math.round(
        ((breakDuration - remainingTime) * 100) / breakDuration
      );
      setProgressPercent(percent);
    } else {
      const percent = Math.round(
        ((focusDuration - remainingTime) * 100) / focusDuration
      );
      setProgressPercent(percent);
    }
  }

  function playAlarm() {
    const audio = new Audio(
      `https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`
    ).play();
  }

  function swapTimers() {
    playAlarm();
    if (!onBreak) {
      setRemainingTime(breakDuration);
    } else {
      setRemainingTime(focusDuration);
    }
    setOnBreak((prevState) => !prevState);
    setProgressPercent(0);
  }

  // Timer starts out paused
  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      if (remainingTime === 0) {
        swapTimers();
      } else {
        setRemainingTime((prevState) => prevState - 1);
      }
    },
    timerState.play ? 1000 : null
  );

  function playPause() {
    if (timerState.play) {
      pauseTimer();
    } else if (timerState.pause) {
      playTimer();
    } else if (timerState.stop) {
      playTimer();
    }
  }

  function handleDurationClick(seconds) {
    return Math.abs(seconds) > 60
      ? setFocusDuration((prevState) => prevState + seconds)
      : setBreakDuration((prevState) => prevState + seconds);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {secondsToDuration(focusDuration)}
            </span>
            <div className="input-group-append">
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => handleDurationClick(-300)}
                disabled={timerState.play || focusDuration === 300}
              >
                <span className="oi oi-minus" />
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => handleDurationClick(300)}
                disabled={timerState.play || focusDuration === 3600}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {secondsToDuration(breakDuration)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => handleDurationClick(-60)}
                  disabled={timerState.play || breakDuration === 60}
                >
                  <span className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => handleDurationClick(60)}
                  disabled={timerState.play || breakDuration === 900}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !timerState.play,
                  "oi-media-pause": timerState.play,
                })}
              />
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              disabled={!timerState.play}
              onClick={stopTimer}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            <Title
              onBreak={onBreak}
              focusDuration={focusDuration}
              breakDuration={breakDuration}
            />
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(remainingTime)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={progressPercent} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${progressPercent}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
